import React from "react";
import firebase from "firebase";
import { CALCULATIONS } from "../../constants.js";

function CalculationsMenu({ cell, cellID }) {
  const onTypeSelected = (e) => {
    firebase
      .database()
      .ref("cells/" + cellID)
      .set({
        ...cell,
        calculation: e.target.value,
      });
  };

  return (
    <>
      <select className="calculation-menu" onChange={onTypeSelected}>
        {Object.keys(CALCULATIONS).map((key) => (
          <option
            value={CALCULATIONS[key]}
            selected={cell.calculation == CALCULATIONS[key]}
          >
            {CALCULATIONS[key]}
          </option>
        ))}
      </select>
      <input type="radio"></input>
      {Object.keys(CALCULATIONS).map((key) => (
        <>
          <input
            type="radio"
            id="bruh"
            value={CALCULATIONS[key]}
            // checked={cell.calculation == CALCULATIONS[key]}
            onChange={onTypeSelected}
          >
            {/* {CALCULATIONS[key]} */}
          </input>
          <label for={CALCULATIONS[key]}>{CALCULATIONS[key]}</label>
        </>
      ))}
    </>
  );
}

export default CalculationsMenu;
