import React, { useState } from "react";
import ReactDOM, { render } from "react-dom";
import "./index.scss";
import firebase from "firebase";

import Header from "./components/header.js";
import Home from "./components/home/home.js";
import Notebook from "./components/notebook/notebook.js";

const PAGES = { homepage: "homepage", notebook: "notebook" };

function Container() {
  const [currentScreen, setCurrentScreen] = useState(PAGES.homepage);
  const [editNotebookID, setEditNotebookID] = useState(null);

  const editNotebook = (notebookID) => {
    setCurrentScreen(PAGES.notebook); 
    setEditNotebookID(notebookID);
  }

  const createNewNotebook = () => {
    const ref = firebase.database().ref('notebooks/');
    const notebookID = ref.push().key;

    editNotebook(notebookID);
    ref.child(notebookID).set({
      title: "",
    });
  }


  switch (currentScreen) {
    case PAGES.homepage:
      return (
        <div>
          <Header />
          <Home editNotebook={editNotebook} createNewNotebook={createNewNotebook} />
        </div>
      );
    case PAGES.notebook:
      return (
        <div>
          <Notebook notebookID={editNotebookID}/>
        </div>
      );
  }
}

const firebaseConfig = {
  apiKey: "AIzaSyDBTVpLIWCa_eD4Y1fNmagEbJ3yJ59oE_g",
  authDomain: "hcil-website.firebaseapp.com",
  databaseURL: "https://hcil-website.firebaseio.com",
  projectId: "hcil-website",
  storageBucket: "hcil-website.appspot.com",
  messagingSenderId: "1089716918653",
  appId: "1:1089716918653:web:8d47f9a294973c7cd01414",
  measurementId: "G-E92GSV896D",
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);
firebase.analytics();

ReactDOM.render(<Container />, document.getElementById("root"));
