import React, { useState, useEffect } from "react";
import CellDescription from "./celldescription";
import firebase from "firebase";

import Chart from "./../../images/Group 5.png";

function Cell({ cellID, notebook }) {
  const [cell, setCell] = useState(null);

  useEffect(() => {
    var cellRef = firebase.database().ref("cells/" + cellID);
    cellRef.on("value", (snapshot) => {
      const data = snapshot.val();
      setCell(data);
    });
  }, [cellID]);

  if (cell == null) {
    return <div className="cell-container">Loading...</div>;
  }

  return (
    <div className="cell-container">
      <div className="cell-options">
        <input className="cell-name" placeholder="Cell name"></input>
        <div className="cell-input">Input: [cellname]</div>
        <div className="cell-type">Cell type: regression</div>
      </div>
      <div className="cell-output">
        <img className="chart" src={Chart}></img>
      </div>
      <CellDescription cell={cell} cellID={cellID} />
    </div>
  );
}

export default Cell;
