import React from "react";
import ReactDOM, { render } from "react-dom";
import { useState } from "react";
import "./index.scss";
import addButton from "./add.svg";
import Header from "./header.js";

function Content({ setCurrentScreen }) {
  return (
    <div className="content">
      <Menu />
      <List setCurrentScreen={setCurrentScreen} />
    </div>
  );
}

function Menu() {
  return (
    <div className="menu-container">
      <h1 className="menu-head"> Explore </h1>
      <h1 className="menu-head"> My work </h1>
      <h3 className="menu-subhead"> Notebooks</h3>
      <h3 className="menu-subhead"> Datasets</h3>
      <h3 className="menu-subhead"> Charts</h3>
    </div>
  );
}

function List({ setCurrentScreen }) {
  return (
    <div className="list-container">
      <div className="list-header">
        <h1>Notebooks</h1>
        <img
          className="add-button"
          src={addButton}
          alt="Add Button"
          onClick={() => setCurrentScreen(PAGES.notebook)}
        ></img>
      </div>
      <ItemContainer />
      <ItemContainer />
      <ItemContainer />
      <ItemContainer />
      <ItemContainer />
      <ItemContainer />
    </div>
  );
}

class ItemContainer extends React.Component {
  render() {
    return (
      <div className="list-item">
        <div className="item-contents-row-one">
          <h3>Adam's Notebook</h3>
          <button className="edit-button wrap">Edit</button>
        </div>
        <div className="item-contents-row-two">
          <p>Last edited</p>
          <p>Yesterday</p>
        </div>
      </div>
    );
  }
}

const PAGES = { homepage: "homepage", notebook: "notebook" };

function Container() {
  const [currentScreen, setCurrentScreen] = useState(PAGES.homepage);

  return (
    <div>
      <Header />
      <Content setCurrentScreen={setCurrentScreen} />
    </div>
  );
}

ReactDOM.render(<Container />, document.getElementById("root"));
