import React, { useState, useEffect } from "react";
import firebase from "firebase";

import AddButton from "./../../images/add-button.svg";

import Cell from "./cell.js";
import NotebookTitle from "./notebooktitle.js";
import NotebookHeader from "./notebookheader.js";

function Notebook({ notebookID, setCurrentScreen }) {
  const [notebook, setNotebook] = useState(null);

  useEffect(() => {
    var notebookRef = firebase.database().ref("notebooks/" + notebookID);
    notebookRef.on("value", (snapshot) => {
      const data = snapshot.val();
      setNotebook(data);
    });
  }, []);

  return (
    <div className="notebook-container">
      <NotebookHeader setCurrentScreen={setCurrentScreen} />
      <div className="page-container">
        {notebook == null ? (
          "Loading..."
        ) : (
          <>
            <NotebookTitle text={notebook.title} notebookID={notebookID} />
