import Notebook from "./components/notebook/notebook";
import { CELL_TYPES } from "./constants.js";

export function mean(input) {
  const output = {};

  for (const columnID in input) {
    const column = input[columnID];

    let sum = 0;
    for (let i in column) {
      sum += column[i];
    }

    const mean = sum / column.length;
    output[columnID] = [mean];
  }

  return output;
}

export function max(input) {
    const output = {};

    for (const columnID in input) {
        const column = input[columnID];

        let max = 0;
        for (let i in column) {
            if (column[i] > max) {
                max = column[i]
            }
        }

        output[columnID] = max;
    }

    return output;
}

export function min(input) {
    const output = {};

    for (const columnID in input) {
        const column = input[columnID];

        let min = 0;
        for (let i in column) {
            if (column[i] < max) {
                min = column[i]
            }
        }

        output[columnID] = min;
    }

    return output;
}

export function dumbdata() {
  const data = {
    columnOne: [1, 4, 6, 5, 3, 2, 35, 6, 7, 4],
    columnThree: [8, 4, 3, 6, 77, 33, 56, 2, 1],
  };

  return data;
}

export function docalculations(cells, cellIDs) {
  const results = {};

  for (const i in cellIDs) {
    const input = (i == 0 ? {} : results[cellIDs[i - 1]]);

    let output;
    const cellID = cellIDs[i];
    switch (cells[cellID].type) {
      case CELL_TYPES.calculation:
        output = mean(input);
        break;
      case CELL_TYPES.chart:
        output = {};
        break;
      case CELL_TYPES.loadData:
        output = dumbdata();
        break;
    }
    results[cellID] = output;
  }
  return results;
}
