import firebase from "firebase";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory,
} from "react-router-dom";
import { getNotebookRoute } from "./routes";

export const useCreateNewNotebook = () => {
  const history = useHistory();

  return () => {
    const ref = firebase.database().ref("notebooks/");
    const notebookID = ref.push().key;

    history.push(getNotebookRoute(notebookID));
    ref.child(notebookID).set({
      title: "",
      cells: [],
    });
  };
};

export const useCreateNewCell = (notebookID, notebook) => {
    const ref = firebase.database().ref("cells/");
    const cellID = ref.push().key;

    ref.child(cellID).set({
      notebookID: "",
      input: "",
      output: "",
      description: "",
    });

    const notebookRef = firebase.database().ref("notebook/" + notebookID);
    notebookRef.set({
        ...notebook,
        cells: [...notebook.cells, cellID]
    }
    )
}
