import React, { useState } from "react";
import firebase from "firebase";

function NotebookTitle({notebookID, text}) {
   const onTextChange = e => {
        firebase.database().ref('notebooks/' + notebookID).set({
          title: e.target.value,
        });
        console.log(e.target.value);
      }

    return (
        <input className="notebook-title" type="text" onChange={onTextChange} placeholder="Enter title..." value={text}></input>
    );
}

export default NotebookTitle;
